import React, { useEffect, useRef } from "react";
import $ from "jquery";
import "datatables.net";
import "datatables.net-responsive";
import "datatables.net-responsive-bs4/css/responsive.bootstrap4.min.css";

const DataTableExample = () => {
  const tableRef = useRef(null);

  useEffect(() => {
    const table = $(tableRef.current).DataTable({
      responsive: true,
    });
    return () => {
      table.destroy();
    };
  }, []);

  return (
    <div className="container w-full md:w-4/5 xl:w-3/5 mx-auto px-2">
      <h1 className="flex items-center font-sans font-bold break-normal text-indigo-500 px-2 py-8 text-xl md:text-2xl">
        Responsive{" "}
        <a className="underline mx-2" href="https://datatables.net/">
          DataTables.net
        </a>{" "}
        Table
      </h1>
      <div id="recipients" className="p-8 mt-6 lg:mt-0 rounded shadow bg-white">
        <table
          ref={tableRef}
          className="stripe hover"
          style={{ width: "100%", paddingTop: "1em", paddingBottom: "1em" }}
        >
          <thead>
            <tr>
              <th data-priority="1">Name</th>
              <th data-priority="2">Position</th>
              <th data-priority="3">Office</th>
              <th data-priority="4">Age</th>
              <th data-priority="5">Start date</th>
              <th data-priority="6">Salary</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>Tiger Nixon</td>
              <td>System Architect</td>
              <td>Edinburgh</td>
              <td>61</td>
              <td>2011/04/25</td>
              <td>$320,800</td>
            </tr>
            <tr>
              <td>Donna Snider</td>
              <td>Customer Support</td>
              <td>New York</td>
              <td>27</td>
              <td>2011/01/25</td>
              <td>$112,000</td>
            </tr>
            {/* Add more rows as needed */}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default DataTableExample;
