import React, { useEffect, useState } from "react";
import axios from "axios";

function Table() {
  const [Etds, setEtds] = useState([]);
  const [EtdsG2, setEtdsG2] = useState([]);
  const [isPresent, setIsPresent] = useState({});

  useEffect(() => {
    axios
      .get("http://localhost:3001/getAggregatedData")
      .then((res) => setEtds(res.data))
      .catch((err) => console.log(err));
  }, []);

  useEffect(() => {
    axios
      .get("http://localhost:3001/getGroupedDataForGroup2")
      .then((res) => {
        setEtdsG2(res.data);

        // Mise à jour de l'état isPresent après récupération des données
        const updatedIsPresent = res.data.reduce((acc, cur) => {
          acc[cur.MatriculeEtd] = true; // Marquer tous les éléments comme présents
          return acc;
        }, {});

        // Mettre à jour les valeurs spécifiques à partir de l'état isPresent actuel
        Object.keys(isPresent).forEach((key) => {
          if (isPresent[key] !== undefined) {
            updatedIsPresent[key] = isPresent[key];
          }
        });

        setIsPresent(updatedIsPresent);
      })
      .catch((err) => console.log(err));
  }, []);

  const handleCheckboxChange = (MatriculeEtd, checked) => {
    // Mettre à jour l'état local après la réussite de l'API
    if (checked) {
      axios
        .post("http://localhost:3001/postEtds", { matricule: MatriculeEtd })
        .then(() => {
          setIsPresent((prevIsPresent) => ({
            ...prevIsPresent,
            [MatriculeEtd]: true,
          })); // Ajouter à l'état local
        })
        .catch((err) => console.log(err));
    } else {
      axios
        .delete(`http://localhost:3001/deleteEtd/${MatriculeEtd}`)
        .then(() => {
          setIsPresent((prevIsPresent) => {
            const updatedIsPresent = { ...prevIsPresent };
            delete updatedIsPresent[MatriculeEtd];
            return updatedIsPresent;
          }); // Supprimer de l'état local
        })
        .catch((err) => console.log(err));
    }
  };

  return (
    <div className="container">
      <div className="table-responsive">
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>#</th>
              <th>Matricule</th>
              <th>Nom</th>
              <th>Prenom</th>
              <th>Presence</th>
            </tr>
          </thead>
          <tbody>
            {EtdsG2.map((Etd, index) => {
              return (
                <tr key={index}>
                  <th scope="row">{index + 1}</th>
                  <td>{Etd.MatriculeEtd}</td>
                  <td>{Etd.nom}</td>
                  <td>{Etd.prenom}</td>
                  <td>
                    <input
                      type="checkbox"
                      aria-label="Checkbox for following text input"
                      checked={isPresent[Etd.MatriculeEtd] || false}
                      onChange={(e) =>
                        handleCheckboxChange(
                          Etd.MatriculeEtd,
                          e.target.checked
                        )
                      }
                    />
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Table;
